# -*- coding: utf-8 -*-import sqlite3conn = sqlite3.connect('database.db')cursor = conn.cursor()cursor.execute('''CREATE TABLE IF NOT EXISTS students                        (id INTEGER PRIMARY KEY, name TEXT, age INTEGER, avg_score REAL)''')def show_students():    cursor.execute("SELECT * FROM students")    rows = cursor.fetchall()    for row in rows:        print(row)def insert_students():    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Maslov Artem Aleksandrovich", 20, 5))    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Smirnov Artem Lukich", 20, 5))    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Vereshhagina Varvara Ivanovna", 20, 5))    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Ustinov Mixail Artyomovich", 20, 5))    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Alekseeva Valeriya Leonovna", 20, 5))    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Zorin Fyodor Artyomovich", 20, 5))    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Ivanov Georgij Matveevich", 20, 5))    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Sorokina Eseniya Pavlovna", 20, 5))    cursor.execute("INSERT INTO students (name, age, avg_score) VALUES (?, ?, ?)", ("Dem`yanov Kirill Yaroslavovich", 20, 5))insert_students()show_students()