# -*- coding: utf-8 -*-import sqlite3, datetimeconn = sqlite3.connect('database.db')cursor = conn.cursor()cursor.execute('''CREATE TABLE IF NOT EXISTS books                        (id INTEGER PRIMARY KEY, title TEXT, author1 TEXT, author2 TEXT, date timestamp)''')def show():    cursor.execute("""SELECT * FROM books WHERE date >= ?""", [datetime.datetime(2019, 1, 3)])    rows = cursor.fetchall()    for row in rows:        print(row)def insert():    cursor.execute("INSERT INTO books (title, Author1, Author2, date) VALUES (?, ?, ?, ?)",                   ("Book1", "Author1", "Author2", datetime.datetime(2019, 1, 1)))    cursor.execute("INSERT INTO books (title, Author1, Author2, date) VALUES (?, ?, ?, ?)",                   ("Book2", "Author3", "Author3", datetime.datetime(2020, 1, 1)))    cursor.execute("INSERT INTO books (title, Author1, Author2, date) VALUES (?, ?, ?, ?)",                   ("Book3", "Author3", "Author3", datetime.datetime(2021, 1, 1)))    cursor.execute("INSERT INTO books (title, Author1, Author2, date) VALUES (?, ?, ?, ?)",                   ("Book4", "Author4", "Author4", datetime.datetime(2022, 1, 1)))    cursor.execute("INSERT INTO books (title, Author1, Author2, date) VALUES (?, ?, ?, ?)",                   ("Book5", "Author5", "Author5", datetime.datetime(2019, 1, 3)))insert()show()