# -*- coding: utf-8 -*-import sqlite3, datetimeconn = sqlite3.connect('database.db')cursor = conn.cursor()cursor.execute('''CREATE TABLE IF NOT EXISTS films                        (id INTEGER PRIMARY KEY, title TEXT, genre TEXT, rating REAL)''')def show_students():    cursor.execute("""SELECT * FROM films WHERE genre = ?""", ["action"])    rows = cursor.fetchall()    for row in rows:        print(row)def insert_students():    cursor.execute("INSERT INTO films (title, genre, rating) VALUES (?, ?, ?)",                   ("Film1", "action", 4.8))    cursor.execute("INSERT INTO films (title, genre, rating) VALUES (?, ?, ?)",                   ("Film2", "ne action", 3.2))    cursor.execute("INSERT INTO films (title, genre, rating) VALUES (?, ?, ?)",                   ("Film3", "action", 14))    cursor.execute("INSERT INTO films (title, genre, rating) VALUES (?, ?, ?)",                   ("Film4", "ne action", 100))insert_students()show_students()