# -*- coding: utf-8 -*-import sqlite3, datetimeconn = sqlite3.connect('database.db')cursor = conn.cursor()cursor.execute('''CREATE TABLE IF NOT EXISTS employers                        (id INTEGER PRIMARY KEY, name TEXT, position TEXT, zp REAL)''')def show_students():    cursor.execute("""SELECT name FROM employers WHERE position = ?""", ["head"])    rows = cursor.fetchall()    for row in rows:        print(row)def insert_students():    cursor.execute("INSERT INTO employers (name, position, zp) VALUES (?, ?, ?)",                   ("Fist Last Middle name", "head", 100500))    cursor.execute("INSERT INTO employers (name, position, zp) VALUES (?, ?, ?)",                   ("Fist2 Last Middle name", "ne head", 100500))    cursor.execute("INSERT INTO employers (name, position, zp) VALUES (?, ?, ?)",                   ("Fist3 Last Middle name", "head", 100500))    cursor.execute("INSERT INTO employers (name, position, zp) VALUES (?, ?, ?)",                   ("Fist4 Last Middle name", "ne head", 100500))insert_students()show_students()